package Loops;

public class EvenOddForLoop2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		System.out.println("program start");
		for(byte i=1; i<11;i++) {
			if(i%2!=0) {
				System.out.println("Odd Number is " +i);
			}
			}
		System.out.println("programs ends");
	}
//	program start
//	Odd Number is 1
//	Odd Number is 3
//	Odd Number is 5
//	Odd Number is 7
//	Odd Number is 9
//	programs ends

}
	
